INSERT INTO `blogs` (`blog_id`, `blog_category_id`, `post_type`, `status_id`, `status`, `episode_id`, `episode_number`, `title`, `description`, `permalink`, `is_published`, `is_post`, `is_selected`, `sticky_on_home_page`, `publish_date`, `viewed`, `featured_image`, `create_date`, `update_date`, `create_by`, `update_by`, `last_modaretion_date`, `last_modarate_by`) VALUES
  (107, 7, 'blog', 0, 'admin-trash', 0, '', 'নক্ষত্র ব্লগের প্রথম পোস্ট। ', '<p>\r\nএটি একটি পরীক্ষামুলক পোস্ট। আপডেট জানার জন্য নোটিশে চোখ রাখুন।<br />\r\nধন্যবাদ	\r\n</p>\r\n', '107', 0, 0, 0, 0, '0000-00-00 00:00:00', 82, '', '2012-09-18 13:27:07', '0000-00-00 00:00:00', 17, 0, '2012-09-27 09:33:38', 1),
  (108, 8, 'blog', 0, 'publish', 0, '', 'প্রেমে তবু মধুর যাতনা আছে দ্রোহে আমাদের দেশে কিচ্ছু নেই', '<p style="margin-left: 40px;">\r\n	<span class="userContent">কেঁচো হয়ে বাঁচো, পাবে মৃত্তিকার ঘ্রান<br />\r\n	নয়তো মেরুদন্ডহীন সর্পের মতো<br />\r\n	দুর্বার গতি আর জীবন নাশের শক্তি<br />\r\n	তোমার করতল গত চিরকাল।<br />\r\n	<br />\r\n	বেঁচে থাকো বানরের মত<br />\r\n	তোমায় দেখে পুলকিত হবে মানব কুল<br />\r\n	তাতে দেখে নিও তৃপ্তি পাবে তুমি<br />\r\n	এটো জল ভালোবাসো গন্ডারের মত<br />\r\n	কারো চিতকারে কি আসে যায়?</span></p>\r\n<p style="margin-left: 40px;">\r\n	তুমি বেঁচে থাকো নিজের মত করে।</p>\r\n<div class="text_exposed_show" style="margin-left: 40px;">\r\n	<br />\r\n	<span class="userContent">নতুবা প্রেম করো শুয়োয়ের মত<br />\r\n	মেতে থাকো বিষ্টা নিয়ে।<br />\r\n	বিষ্টার গন্ধে খুজে নাও জীবনের সুখ<br />\r\n	মানুষ হয়োনা যেন।<br />\r\n	<br />\r\n	মানব কুল আজ বড় দিশেহারা<br />\r\n	তারা প্রেমে পড়ে, তারা দ্রোহে মরে<br />\r\n	পাওয়ার তাগিদে মরে, না পাওয়ার ব্যথায় কাঁদে।<br />\r\n	তারা মরে ভালবেসে, মরে যায় দ্রোহে।<br />\r\n	&quot;প্রেমে তবু মধুর যাতনা আছে<br />\r\n	দ্রোহে আমাদের দেশে কিচ্ছু নেই!&quot;<br />\r\n	প্রতিদান হীন কষ্ট যাতনা বিনে।</span></div>\r\n', '108', 0, 0, 1, 0, '0000-00-00 00:00:00', 68, 'Array', '2012-09-18 13:52:23', '2012-09-18 14:15:48', 18, 18, '2012-09-23 20:27:21', 1),
  (109, 7, 'blog', 0, 'admin-trash', 0, '', 'তথ্য প্রযুক্তিতে বাংলাদেশের', '<p>\r\n	&lt;script&gt;document.write(&quot;OK&quot;)&lt;/script&gt;</p>\r\n', '109', 0, 0, 0, 0, '0000-00-00 00:00:00', 47, '', '2012-09-18 14:27:40', '0000-00-00 00:00:00', 16, 0, '2012-09-23 20:11:35', 1),
  (110, 9, 'blog', 0, 'admin-trash', 0, '', 'SEO for Bangladeshi', '<p>\r\n	&nbsp;</p>\r\n<p style="margin: 15px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14.44444465637207px; vertical-align: baseline; color: rgb(26, 26, 26); font-family: Signika, sans-serif; line-height: 22.38888931274414px; ">\r\n	It will be a great place to start SEO in Bangladesh. I have discovered few points for my thoughts.</p>\r\n<h2 style="margin: 10px 0px 13px; padding: 0px; border: 0px; outline: 0px; font-size: 21px; vertical-align: baseline; color: rgb(6, 59, 89); line-height: 1.5em; font-weight: 100; font-family: Signika, sans-serif; ">\r\n	Why you will start your website&rsquo;s SEO in Bangladesh?</h2>\r\n<p style="margin: 15px 0px; padding: 0px; border: 0px; outline: 0px; font-size: 14.44444465637207px; vertical-align: baseline; color: rgb(26, 26, 26); font-family: Signika, sans-serif; line-height: 22.38888931274414px; ">\r\n	See the points below which will help you to think about your website&rsquo;s traffic.</p>\r\n<ul class="bullet_arrow2" style="margin: 0px 0px 0px 30px; padding-right: 0px; padding-left: 0px; border: 0px; outline: 0px; font-size: 14.44444465637207px; vertical-align: baseline; color: rgb(26, 26, 26); font-family: Signika, sans-serif; line-height: 22.38888931274414px; ">\r\n	<li style="margin: 7px; padding: 0px 0px 0px 25px; border: 0px; outline: 0px; font-size: 14.44444465637207px; vertical-align: baseline; background-image: url(http://www.shahariaazam.com/wp-content/themes/dandelion/images/arrow2.png); background-color: transparent; list-style: none; line-height: 18px; background-position: 0px 0.2em; background-repeat: no-repeat no-repeat; ">\r\n		Low competition</li>\r\n	<li style="margin: 7px; padding: 0px 0px 0px 25px; border: 0px; outline: 0px; font-size: 14.44444465637207px; vertical-align: baseline; background-image: url(http://www.shahariaazam.com/wp-content/themes/dandelion/images/arrow2.png); background-color: transparent; list-style: none; line-height: 18px; background-position: 0px 0.2em; background-repeat: no-repeat no-repeat; ">\r\n		<img alt="" src="http://i3.ytimg.com/vi/2PtQJao5vks/mqdefault.jpg" style="width: 320px; height: 180px; " /><br />\r\n		In Bangladesh you can get your site high because of low competition because most of the popular websites aren&rsquo;t practicing SEO or implementing SEO strategy yet!! So it&rsquo;s your chance to do SEO for your website before the techniques smells out for other popular website.</li>\r\n</ul>\r\n', '110', 0, 0, 0, 0, '0000-00-00 00:00:00', 20, 'Array', '2012-09-18 14:43:33', '0000-00-00 00:00:00', 16, 0, '2012-09-21 12:48:28', 1),
  (111, 7, 'blog', 0, 'publish', 0, '', 'হায় বিটিভি............... ', '<p>\r\n	<span style="color: rgb(51, 51, 51); font-family: ''lucida grande'', tahoma, verdana, arial, sans-serif; font-size: 13px; line-height: 17px; ">বিটিভি এটা কি ধরনের রসিকতা শুরু করল আমাদের সাথে? ...... বাচ্চারা খালি গণতন্ত্র গণতন্ত্র করে গলাবাজি করে, এখনতো গণতন্ত্র মানে হল জনগণকে পিষ্ট করার যন্ত্র.................. :@</span></p>\r\n', '111', 0, 0, 1, 0, '0000-00-00 00:00:00', 91, '', '2012-09-19 03:56:21', '0000-00-00 00:00:00', 21, 0, '2012-09-25 09:38:29', 1),
  (112, 7, 'blog', 0, 'publish', 0, '', 'এ গোলামী আর কত দিন', '<p>\r\n	কথায় আছে রাজনীতি মানে রাজার নীতি। রাজা রাজ্য পরিচালনার জন্য প্রজাদের কল্যানে নীতি প্রয়োগ করেন। আসলে সেটাই রাজনীতি। এইটা হইলো সাধারন কথা। কিন্তু এখন আর সেই দিন নাই। রাজাও নাই প্রজাও নাই। কিন্তু রাজনীতি আছে। সময়ের ব্যবধানে মানুষ নিজেরে সভ্য দাবী করলেও কতখানি হইতে পারছে সেইটা বিবেচ্য বিষয়। কারন রাজনীতি এখন আর রাজকীয় ব্যপারে সীমাবদ্ধ নাই। ক্ষমতাসীনের সর্বোচ্চ নেতা থেইকা শুরু কইরা পাড়ায় মুদিদোকানদারও এখন রাজনীতি করে। তাতে রাস্ট্রের কোন উপকার হোক আর না যে দিকে মেঘ সেই ছাতা ধরার রাজনীতি তারা নিজেরা লাভবান হইতেছ&nbsp; তাতে কোন সন্দেহ নাই। আমরা বাঙ্গালী। দেশ প্রেম অন্তরে যাই থাকুক প্রতিক্রিয়াশীল হইতে দেশ প্রেম না থাকলেও চলে। প্রতিক্রিয়ার গতি এত বেশী বোজার উপায় নাই এইটা দেশ প্রেম নাকি নিজ প্রেম। তবে আমরা এদিক দিয়া আবার বেশী চালাক। মেঘের বিপরীতে যে ছাতা ধরি আর তেলা মাথায় তেল তালি এই টা প্রমান হইতেও বেশী সময় লাগেনা। আসলে মুদিদোকানীর আর কি দোষ? রাজারা আগে রাজা ছিল এখনো আছে। দেশ চলে সরকার নামের অঘোষিত রাজা দিয়া যারে তার অনুসারী প্রজারা ভোট নামক সমর্থন দিয়া রাজা বানায়।&nbsp; প্রতিটি প্রজাই নিজের রাজার গুনগান নিয়া ব্যস্ত। রাজা কোন দোষ করলে তার চোখে ধরা পড়েনা। কারন আসলে বাঙ্গালীর রক্ত নাকি দুষিত হয়া গেছে। চুম্বুকের সাথে লোহা ঘষলে যেমন লোহাটা চুম্বুকের মত আচরন করে ঠিক তেমনি গোলামী প্রিয় বাঙ্গালী জাতি গোলামীর কাজ ঠিক মত করতে পারে। যেই যে গোলামীর করার অভ্যাসটা বৃটিশরা কইরা দিয়া গেছে,বৃটিশ গেছে,&nbsp; দিনও গেছে কিন্তু গোলামী করার সভাব যায় নাই। আমরা একটা সাধীন জাতি। আমাদের যে যার নুন খাই তার গুন গাওয়ার মত গোলামীর দিন নাই, কিংবা সত্যকে সত্য আর মিথ্যা বলার দিন আছে&nbsp; সেইটা মনে করার চেস্টা করিনা।</p>\r\n<p>\r\n	&nbsp;</p>\r\n', '112', 0, 0, 1, 0, '0000-00-00 00:00:00', 40, '', '2012-09-19 06:20:44', '0000-00-00 00:00:00', 18, 0, '2012-09-19 06:22:48', 1),
  (113, 7, 'blog', 0, 'publish', 0, '', 'বেগুন বাড়ী বস্তিতে ভয়াবহ আগুন', '<p>\r\n	আজ সকালে বেগুন বাড়ী বস্তিতে ভয়াবহ আগুণ লেগেছে। কয়েক শত বাড়ি ঘর পুড়ে গেচে। ঘর হারা হয়েছে কয়েক হাজার মানুষ। দমকল বাহিনী পৌছে আগুণ নেভানোর চেস্টা করচে। ধারনা করা হচ্ছে&nbsp; কারো মনে দুখের আগুন থেকে এই আগুন কান্ডের সুত্রপাত।</p>\r\n', '113', 0, 0, 0, 0, '0000-00-00 00:00:00', 27, '', '2012-09-20 01:10:57', '0000-00-00 00:00:00', 26, 0, '2012-09-20 01:11:40', 1);
INSERT INTO `blogs` (`blog_id`, `blog_category_id`, `post_type`, `status_id`, `status`, `episode_id`, `episode_number`, `title`, `description`, `permalink`, `is_published`, `is_post`, `is_selected`, `sticky_on_home_page`, `publish_date`, `viewed`, `featured_image`, `create_date`, `update_date`, `create_by`, `update_by`, `last_modaretion_date`, `last_modarate_by`) VALUES
  (114, 8, 'blog', 0, 'publish', 0, '', 'যে চিঠি শুধু লেখার নদীতেই ভেসেছিলো সময়ের নৌকা হয়ে', '<p>\r\n	<a href="http://media.somewhereinblog.net/images/thumbs/aruddho007_1269596703_1-8888888888888888888888888888888.gif" target="_top"><img alt="" src="http://media.somewhereinblog.net/images/thumbs/aruddho007_1269596703_1-8888888888888888888888888888888.gif" style="width: 373px; height: 414px" /></a></p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	&nbsp;</p>\r\n<p>\r\n	মমি,</p>\r\n<p>\r\n	কি,রে তুই? &nbsp; চিঠিটা হাতে পেয়েই খুলে ফেললি;<br />\r\n	শোন; আমি প্রেম পত্র জাতীয় কিছু লিখিনি অত ভয় পাবার মতো কিছু নেই আমি নিত্যান্তই কাজ না থাকায় অবসরে বসে লিখেছি এই চিঠিটা। কেমন আছিস তুই? ভালো না থাকলেও তো বলবি ভালো আছিস এইটে তোর একটা বদস্বভাব।<br />\r\n	তুই কি,রে চিঠিটাকে এতো শক্ত করে ধরে রেখেছিস কেন ব্যাথা লাগেনা বুঝি ? আমি তো লিখার সময় খুব যত্ন করে লিখেছি আমি জানি তুই সবসময় চিঠি খুব মনযোগ দিয়ে পড়িস তাই তো যত্ন করে লিখা এই চিঠি তাই বলে ভাবিস না, আমি একেবারে গুরুত্বপূর্ণ কিছু লিখে পাতা ভরে ফেলেছি আমি কিন্তু বেশ কিছু বেহুদা কথার সমাহার দিয়ে সাজিয়েছি আজ।<br />\r\n	তুই আমাকে বলেছিলি আমি যেন ভালভাবে চলি; আমি তো সেই কবে থেকেই ভালো হয়ে গেছি তা কি তুই জানতি না; ইচ্ছে করে হলেও তো আমি আর খারাপ থাকতে পারছিনে।<br />\r\n	<br />\r\n	আমি অনেকটা সোডিয়াম লাইটের মতো প্রতিদিন একবেলা করে আমাকে প্রয়োজন হয়; আর তখন আমার কদর বেড়ে যায় তারপর সারাদিন আমার নিরব বসে থাকা; কেউ দেখেইনা। তোর মন খারাপ থাকলে আমি কারণ খুজি, কেন মন খারাপ ? তুই তো অনেক সময় বলিস না, আমি জানি আমার কাছে লুকানো আছে তোর অনেক কথা; জানিনা তবু কেন তুই অনেক কিছু চেপে যাস।<br />\r\n	আমি তো মনতাত্বিক নই যে তোর না বলা কথা গুলো উদঘাটন করতে পারবো; মাঝে মাঝে অনেকটা চেষ্টা চালাই কিন্তু পাইনা , তবে আমাকে কেন বলিস না? আমি কি মন খারাপ করা কারণ শুনলে কষ্ট পাবো; যদি পাই তবুও ভালো লাগবে; আমাকে বলিস কিন্তু ,কেন তোর মন খারাপ?<br />\r\n	<br />\r\n	আচ্ছা কালো রং কেন এতো প্রিয় তোর আমি তো জানি কালো রং শোকের, আমার জানা মতে তোর তো কোন শোক নেই। মা,বাবা,বোনদের নিয়ে তো তুই ভালই আছিস তুই বলেছিস বেশ কিছু লোক বলে তাদের প্রিয় রঙ নীল সত্যি কি তাই নাকি কষ্টটাকে আকঁড়ে ধরে বেঁচে থাকার ব্যার্থ প্রয়াস মাত্র; তুই তো জানিস এসব ভবনা নিয়ে ভাবতে গেলে আমার নিজের মাঝেও কষ্ট এসো জড়ো হয়। তাই আর আমি ভাবিনা তবে কালো রং কেন প্রিয় আমায় কি সেইটে বলবি,নে।<br />\r\n	<br />\r\n	তুই বলেছিলি যদি কখনো তোদের ওখানে যাই আমাকে নিয়ে ঘুড়বি; একটা সত্যি বলবো আমি মেয়েদের সাথে রিকসায় উঠেছি তবে সেটা নিত্যান্তই প্রয়োজনে কখনো এমন বেহুদা ঘোড়াঘুড়ি করবার জন্য নয়; তবে খুব বেশি যে প্রয়োজনেও উঠেছি তা কিন্তু নয়, এ যাবত কালে আমি তিনবার উঠেছি একদিন শ্যামা দি&rsquo;র সাথে; শ্যামা কে চিনতে পারিস নি বোধহয়, তোর অবিশ্যি চিনতে পারার কথা নয় আমার আপন না হলেউ পরিচিত এক কাকী&rsquo;র মেয়ে আমার থেকে তিন বছরের বড়ো, তার উপর আবার ইয়া মোটা এক রিকসার তিনভাগ তো তার দখলেই চলে গেছিল, আমি কাচুমাচু হয়ে বসেছিলাম সেটা বছর পাঁচেক আগের কথা।<br />\r\n	<br />\r\n	তারপরা আবার রিকসায় উঠেছিলাম দীপা&rsquo;র সাথে দীপা&rsquo;র কথা নিশ্চই মনে আছে তোর ওই যে হাজং মেয়েটি যে আমার বন্ধু হয়েছিলো কথা বলার দুই বছর পর, দু&rsquo;টো বছর এমনি এমনি কথা বললাম তারপর তার মনে হয়েছিলো আমি তার বন্ধু হতে পারি। দেখেছিস মেয়েদের বিচিত্র নমুনা; থাক্ তোকে আবার বেশি কিছু নমুনা দেখাতে গেলে ক্ষেপে যাবি। তো আমি ডিসেম্বরের শেষ দিনটিতে সকালে ওকে আনতে গেছিলাম সোপাদের বাসা থেকে প্রথমে তো আমি দু&#39; টো রিকসা ডাক দিয়েছিলাম; রিকসা দেখে বলল এ,কি আদিখ্যেতা আমার সাথে একসাথে যেতে কি সমস্যা তোমার, আমি বললাম না মানে তোমার পরিচিত কেউ দেখলে কি বলবে? এতো কথা না বলে রিকসায় উঠো।<br />\r\n	এমনিতেই শীতের সকাল তার উপর আমার প্রচন্ড কাশ হয়ে গেছিল্ রিকসায় বসে কাশতে কাশতেই আমি কখন যে গন্তব্য এসছিলাম নিজেই টের পাইনি দীপা বলেছিলো কি ব্যাপার অমন জড়োসড়ো হয়ে বসে আছো কেন?। আর এতো ঠান্ডা লাগালে কিভাবে? অষুধ কেননি? নিজের প্রতি তো খেয়াল দাওনি কখনো এতা উদাসীন হলে চলে।<br />\r\n	আমি বলেছিলাম যার জীবনটাই ভেসে ভেসে কেটে গেলো এতোটা কাল তার আর নিজের প্রতি খেয়াল দিয়ে হবেটা কি, আর তুমি তো জান আমার নিজের প্রতি কখনো মায়া ছিলনা আর কোনদিন হবে কি,না তাও তো বলতে পারিনা। নাহ্ সেদিন আমাকে রিকসা থেকে নামতে না দিয়েই নিজে নেমে গিয়ে ফার্মেসী থেকে আমার জন্য অষুধ কিনেছিলো। তোরা সবাই আমাকে একটু আধটু যে মন থেকে আপন ভাবিস ভালোবাসিস সেটা ভাবতেই আমার ভালোলাগে।<br />\r\n	<br />\r\n	তুই এতক্ষন পড়ে নিশ্চই বিরক্ত হচ্ছিস তিন নাম্বার ব্যাক্তির কথাটা আমি লিখছিনা কেন; ধীরে বন্ধু আমি লিখবো তার আগে বল এখন আপাতত হাতে কোন কাজ আছে? থাকলে সেটা করে এসে তারপর পড়; আর যদি রাতে নিঝুম একটা সময় পাওয়া যায় তাহলে পড়িস; কিরে তারপরও পড়ছিস আজ কাজ নেই মনে হয়।<br />\r\n	আচ্ছা তাহলে এবার তিন নাম্বার ব্যাক্তির কথাটা শোন সেই ব্যাক্তিটি আর কেউ নয় তোর পরিচিত একজন সে হলো; জানি কথাটা পড়েই বলবি আমি আগেই জানতাম? তৃতীয় ব্যাক্তিটি মিলি; কি,রে অমন বোকার মতো মুখ খানা করেছিস কেন? যে কিনা নারীদের কে ভাবতো অসাধারণ; দুর থেকে দেখা যায়, কিন্তু একসাথে চলা ওহ্ ! অসহ্য একেবারেই বিরক্তিকর। দেখ রাগিস না আবার ,আমি তোর রাগকে ভয় পাই।<br />\r\n	আমি যখন প্রথম দেখা করি ওর সাথে ও এমন ভাব ধরলো না যেন কতো দিনের চেনাজানা পরে অবশ্যি আমি একদিন জিজ্ঞাসা করেছিলাম; মিলি বলল আরে তখন পরিচয় পর্ব বলতে গিয়ে চারপাশে ভীড় জমে যেতো বুঝতে পারতো এটা প্রথম দেখা তুমি দেখনি গ্রামের লোকজন কেমন ভ্যাবাচাকা চোখে চাইছিল বারবার আমাদের দিকে। মিলি আমাকে হালকা ধমক দিয়ে রিকসায় উঠিয়েছিলো আমি উঠেই হাসছিলাম বোকার মতো আমাকে বলেছিলো হাসছি কেন; আমি বললাম আনন্দে।<br />\r\n	<br />\r\n	সেদিন রিকসা করে অনেকখানি পথ আমরা গেছিলাম; ওর তো পুরো শরীর মুখ বোরকায় আবৃত্ত;<br />\r\n	এই থাম . . . . . . . .মমি; দেখতো শব্দটা কি ঠিক লিখলাম কিনা আবৃত্ত হবে কি শব্দটা ? না হলে যা হয় তা দিয়ে পড়ে নিস। জানিস আমি একটা গানও গেয়েছিলাম ওর অবশ্যি গানে তেমন একটা রুচি ছিলনা, তুই হলে তো দারুন ইনজয় করতি; এই যা ইংরেজী শব্দ লিখে ফেললাম তুই বল এখানে উপভোগ্য লেখার চাইতে ইনজয় লিখে কি বেমানান লাগছে ? আমি জানি তুই মনে মনে বলছিস এক মিনিটের কথাটা তোকে পাচমিনিট ঘুড়িয়ে পড়িয়ে নিচ্ছি।<br />\r\n	সে, কি&rsquo; রে তুই সামনের দিকে তাকালি কেন চিঠি&rsquo;র দিকে চোখ রাখ। আমি রিকসা থেকে নেমেই টাকা দিলাম রিকসাওলাকে তারপর সেই সময় মনে হলো তোর কথা ফোন দিয়ে দেখি তোর সেলফোন বন্ধ।<br />\r\n	<br />\r\n	মমি একটু অপেক্ষা আমি একটু পানি খেয়ে নেই তারপর লিখি.............<br />\r\n	জানিস আমার ভীষণ ইচ্ছে করে, তোদের পরিচিত সেই কফি হাউসে গিয়ে একদিন জম্পেস আড্ডা দেবো ; আমাদের এ বাজারে কফি তো দুরে থাক ভালো চা পাওয়ায় দুস্কর। বাজারে যে নেসক্যাফে কফির প্যাকেট কিনতে পাওয়া যায় তা দিয়ে রোজ খাওয়া আমার সাধ্যের বাইরে সেইটে তো ভালো করেই জানিস; দেখিস এ কথা শুনে আবার তুই কফি কিনে পাঠাস না যেন। আমি কিন্তু তাহলে ওটা ফেরত পাঠাবো; তোর লেখা ইংরেজী চিঠি পড়তে গিয়ে আমার দাঁতখানিকটা আহত হয়েছে বাংলাই ভালো পারিনা আবার ইংলিশ। আচ্ছা তুই এতো ভীতু কেন্ বলতো চিঠিতে তো তোর কোন নাম বা ঠিকানা কিছুই দেসনি।<br />\r\n	<br />\r\n	আমি কিন্তু আজকাল নিজেকে তোর মতো গুটিয়ে নিতে শিখে গেছি বিজয় দিবসে আমার যে কবিতাটা পত্রিকায় বের হয়েছিলো তা পড়ে কেউ কেউ আবার খোজ করেছে কে সেই লেখক? তুই বল আমি কি পরিচয় দেওয়ার মতো সে রকম লোক হয়েছি! তা আবার আমার পাড়ার লোকেরা কিন্তু আজ অবদি জানেনা যে আমি অখ্যাত কোন এক বস্তাপচাঁ লেখুয়া। কি,রে রাগছিস ?........... না দোস্ত রাগ করেনা ললিপপ কিনে দেব তোমায়। বুঝতে পারছি তুই ভীষন রেগে গেছিস; ঠিক আছে আর পড়তে হবেনা; পড়ে পরিস...............।<br />\r\n	<br />\r\n	কি করছিস রে ? তখন চিঠি পড়ে রেগে গিয়েছিলি; চেয়েছিলি আমাকে একটা মস্তবড় ঝাড়ি দিবি তা আর হলোনা কারণ আমার সেলফোন টা আজ বন্ধ চার্জ নাই দেখিস ভাবিস নে যেন তোর ঝাড়ি&rsquo;র ভয়ে আমি সেলফোন বন্ধ করে রেখেছি, সত্যি বলছি চার্জ নেই জানি আমার কথা তোর বিশ্বেস হচ্ছে না। মিথ্যে কথা আমি খুব একটা বলিনা তবে প্রায়ই একটা মিথ্যে বলতে হয় আর সেটা হলো আমার দুপুরের খাওয়া; আমি তো প্রায়ই দুপুরে খাইনা কিন্তু যখন জিজ্ঞেস করিস আমি তো মিথ্যে বলি; তবে প্রতিদিন না; আমি আসলে বলতে চাইনা, কিন্তু তুই কিংবা মিলি তো নাছোরবান্দা খাইনি জানলে আর রক্ষে নেই।<br />\r\n	<br />\r\n	<br />\r\n	কি,রে খারাপ লাগছে আমার কথা শুনে, তোর মনে আছে একদিন বলেছিলি তুই কখনো সামনা সামনি কোন ছেলেকে কাদঁেত দেখিস নি তবে যা পেয়েছিস তা, আবার সেলফোনে তাও আবার সেটা আমার কান্না; আমি দু&rsquo;টো জিনিস ভালো করে পারিনা সেটা হলো কাদঁতে আর মিথ্যে বলতে; দু&rsquo;টোতেই আমার অস্বস্তি বোধ হয়। তবে আমি এবার কথা দিচ্ছি এখন খুব কম চেষ্টা করবো কথা না রাখতে! জানি এবারো তোর মন মানছে না আমি কি করবো বল ?<br />\r\n	আচ্ছা ঠিক আছে আর এমন হবেনা !<br />\r\n	<br />\r\n	জানিস এখনো আমার মনের পটে ভাসে তোর সাথে প্রথম যেদিন কথা হয়েছিলো সে দিনটির কথা। আমি তখন শ্যাওলা ঢাকা পুকুর পাড়টায় দাড়িয়ে ছিলেম ,সে রাতে আকাশে চাঁদ ছিলনা , অন্ধকারে হাটতে গিয়ে বোধহয় দু একবার হোচট খেয়েছিলাম ,একবার হাত ফসকে সেল ফোনটাও পড়ে গেছিলো।<br />\r\n	রাতে লুকিয়ে কথা বলার জন্য পুকুর ঘাটই আর সেই ভুতোমার্কা তালগাছই আমার একমাত্র ভরসা ছিলো। এক মেসেজে তুই লিখেছিলি তালগাছ থেকে পেত্নিপ্রিয়া এসে নেমে আমার ঘাড় মটকাবে; সত্যি বলতে কি জানিস সেই তের বছর বয়স থেকেই রাত দুটো তিনটে পর্যন্ত বসে থেকেছি তালগাছ তলায় কিন্তু একদিনও পেত্নি প্রিয়তমার দেখা পাইনি বুঝলি ! এইটে আমার আফসোসও বলতে পারিস।<br />\r\n	<br />\r\n	সে রাতে পুকুর ঘাটে আমি বসে বসে কথা বলছিলাম কিন্তু কার সাথে যে বলছিলাম তা আজ এ অবেলায় ঠিক মনে করতে পারছিনা , সেদিন আমি খুব জম্পেশ মুড নিয়ে আমি কথা বলছিলাম; সেলফোনে কল ওয়েটিং অন থাকায় একটু পর পর অচেনা নাম্বার থেকে মিসড্ কল আসছিলো একবার ,দু&rsquo;বার এভাবে প্রায় পনের ষোল বার চলে আসছিলো আমার সহ্যের বাধ ভেঙ্গে গেছিলো এখানে একটা কথা না বললেই নয় তুই জানিস আমার অনেক সহ্য ক্ষমতা তবে কেন সেদিন আমার এতো অল্পতেই এমন হলো সেটা আমি নিজেই জানিনা তাই তড়িঘড়ি করে আমি কথা বলা শেষ করেই অচেনা সেই নাম্বারে দিলাম কল;<br />\r\n	<br />\r\n	আমি একটু অবাক হয়েছিলাম তোর সিমে &ldquo;আমার টিউন&rdquo; সেট করা ছিলো আর টিউনটা আমার প্রিয় গানেরই কয়েকটা কলি ভাবলাম ফোন রিসিভ না করলেও সমস্যা নেই শুনতো তো ভালই লাগছে কিন্তু না; এ,যে বিধি বাম তুই আচমকা ফোন রিসিভ করে বস্লি আর শুনে ফেললি আমার কাক কন্ঠে গাইছি তোর টিউনে সেট করা গানটি;<br />\r\n	তারপর ; আহামরি সে বিস্ময় যথারীতি হাই হ্যালো; নিজের নাম বলতে গিয়ে তোর যতো আপত্তি; জানিনা কি কারনে সেদিন অমন করছিলি তবে সেদিন তোর লুকোচুরি কথা আমার ভালই লেগেছিলো।<br />\r\n	আচ্ছা বন্ধুত্ব করতে চাইলে গান গাওয়া জানতে হবে এ রকম আজিব সিস্টেম তুই কোত্থোকে পেলি ,নাকি তোর নিজের সিষ্টেম এটা। আমি গান প্রিয় মানুষ হলেও গান গাওয়ার কোন চেষ্টা আমি কখনো করিনি আর আমাদের বংশে কেউ কোন কালে গান শিখেছিলো কিনা তাও জানা নেই। আমি গান প্রিয় মানুষ হলেও গান শেখার চেষ্টা কখনো করিনি; তবে আমরা সবাই কিন্তু কমবেশি বাথরুম সিঙ্গার।<br />\r\n	তুই যেমন আমার বাশ মার্কা গলায় গান শুনতে চাস সেরকম কিন্তু কেউ চায়না। এটা মাঝে মাঝে ভাবি ,কিন্তু আজকাল আর এই বাশ মার্কা গলায় গাইতে পারিনা কষ্ট হয়।<br />\r\n	আমার কথা শুনে চোখ বড় করলি কেন! আমাকে কি আচঁড় কাটতে ইচ্ছে করছে তোর ; আমি বুঝতে পারছি ভীষণ রকমের ইচ্ছে করছে কিন্তু এখন কি করবি আমি যে শত মাইল দুরে। মনে মনে বলছিস যাহ্ শালা তোকে আর গান গাইতেই হবেনা; অত্ত তোষামদি করতে পারবোনা বাপু, আর হাত কচলে বলছিস আজকাল আমি বড্ড ধারিবাজ হয়েছি; তুই গান গাওয়ার কথা বললেই আমি বিভিন্ন অজুহাত দেখাই এই যেমন, আশে পাশে লোক আছে, আমি এখন ষ্টুডিও তে, রুমে মা বসে আছে, নীরব কষ্টে আছি; এইসব এই জাতীয় অজুহাত আমারা পুরোনো হয়ে গেছে তাইনা। ভাবছি নতুন কি অজুহাত দেয়া যায়, কি বলিস?<br />\r\n	<br />\r\n	তবে মাঝরাতে আমার বাশমার্কা নিচু গলায় গান শোনাতে গিয়ে কম বিপত্তিতে যে পড়িনি তা কিন্তু নয়। ওই যে সেবার পুজার আগে দাদা বাসায় এলো তুই কিন্তু সেটা জানতিস; রাতে দু&rsquo;জন একসাথে ঘুমিয়েছি ,মনে হয় রাত তখন দুইটে কি আড়াইটে বাজে; হঠাৎ আমার ঘুম ভাঙ্গতেই তোকে দিলাম কল ভাবলাম তুই ঘুমিয়ে গেছিস কিন্তু কি সর্বনাশ তুই মিসড্ পেয়েই সাথে সাথে কলব্যাক করেছিস ; একবার ভাবলাম ধরবো না আবার মনে পড়লো না ধরলে সকালে আমাকে মস্ত ঝাড়ির সম্মুখিন হতে হবে তারচেয়ে কথা বলি; কিন্তু কে জানতো এমন হবে।<br />\r\n	প্রথমে ফিসফিস করে কথা ভালই চলছিলো বিপত্তি বাধলো যখন মাঝ রাতে বলে বসলি তোকে গান শোনাতে হবে!<br />\r\n	<br />\r\n	আমি তোকে কতো বোঝালাম কিন্তু কে শোনে কার কথা তোকে বললাম আমার প্রবলেম আছে কিন্তু সেটা বুঝেও তো বুঝবি না সেটা বুঝতে পারলাম।<br />\r\n	শেষ পর্যন্ত আমি গাইলাম কথায় আছেনা যেখানে বাঘের ভয় সেখানেই রাত হয়। আমার হলো সেই দশা গান মনে হয় পুরোটা শেষ করতে পারিনি। দেখি পাশ থেকে দাদা উঠে বসেছে; আমি ভয়ে ফোনের লাইন দিলাম কেটে এক চাপে ফোন অফ; নিশ্বাস বন্দ করে মরার মতো পড়ে রইলাম।<br />\r\n	<br />\r\n	দাদা উঠে রুমের লাইট অন করলো মনে মনে ভাবলাম আজ আমার প্রেষ্টিজ আকাশের ঘুড্ডি হলো। দাদা আমাকে ডাকলো কি হয়েছে তোর স্বপ্ন দেখছিলি নাকি ঘুমের মধ্যে গান গাইছিলি কেন; ঘাপটি মেরে পড়ে রইলাম যেন গভীর ঘুমে অচেতন। দাদা আবার ডাকলো কি,রে উঠ আমি চোখ কচলে উঠে বসলাম ভাব মেরে বললাম কি হয়েছে, যেন এই মাত্র আকাশ থেকে পড়েছি। দাদা বললো ঘুমের মাঝে গান গাইছিস এইসব হচ্ছেটা কি? আমি ও তাই নাকি কি জানি সারাদিন গান শুনিতো তাই হয়তো স্বপ্নে গান গাইছিলাম। দাদা বললো মারবো এক থাপ্পর তাহলে গানের ভূত ছোটে যাবে।<br />\r\n	এবার তুই বল আমার কি অবস্থা; দেখিস আবার ভুল বোঝিস না যেন।<br />\r\n	তোকে তো আমি আগে একদিন বলেছিলাম । আমি তোকে যতখানি বিশ্বাস করি কিংবা আমি তোকে যতখানি ভয় পাই মনে হয়না আর কেউ তোকে এতো ভয় পায় কিংবা তোকে বিশ্বাস করে; সব কিছু বলে দেয় শেষে আমার মতো হ্যাবলাকান্ত হয়ে যায়।<br />\r\n	আর মাঝে মাঝে তোর ঝাড়ির দুর্দান্ত শিকার হয়।<br />\r\n	<br />\r\n	তবে একটা কথা স্বীকার না করলেই নয় আমার মতো এই গেয়ো ভূতের জন্য সোসাইটি গার্ল হয়েও তুই যতখানি সেক্রিফাইস করিস সেইটে মনে হয় আর কেউ করেনা। জানি কথাটা পড়ে ঠোট উল্টে বলছিস মিথ্যে বললাম তোর চেয়ে অপূর্ন কিংবা মিলি আপু আমাকে সেক্রিফাইস করে বেশি কিন্তু ভূল ওরা আমাকে ভালবাসে ঠিক আছে; কিন্তু কথায় আছে না নাগিনী ছোবল দিলে হয়তো বাচার কোন সম্ভবনা থাকে কিন্তু নারী ছোবল দিলে বাচাঁর কোন সম্ভবনা নেই;সেটা সারা জীবন ভর জ্বালায় পোড়ায় দিনের পর দিন কাটে সে দুঃসহ্য যন্ত্রণা।<br />\r\n	<br />\r\n	আমি উপরের লাইনটা পুর্ন করে লিখলাম না কারণ তোর আস্তে আস্তে আবারো রাগ চরমে উঠছে! প্লিজ শান্ত হো দোস্ত রাগ করিস না তুই রাগ করলে আমি বাশমার্কা গলায় গান শোনাবো কাকে বল ?<br />\r\n	আচ্ছা তুই বলতে পারিস আমার মতো এমন মানুষ তুই পাবি কোথাও। পাবি না; কেননা আমার মতো করে তোকে এভাবে ক্ষণে ক্ষণে রাগাতে পারবেনা কেউ; পারবেনা তোকে হাসাতে; কখনোবা ইচ্ছাকৃত কষ্টের দূয়ার থেকে তোকে ঘুড়িয়ে আনতে কেউ পারবেনা।<br />\r\n	কেউ পারবেনা ইলশে ঘুড়ি বৃষ্টিতে ভিজে ভিজে তোকে কবিতা শোনাতে; পারবেনা কেউ প্রচন্ড শীত কে আগ্রাহ করে তোকে গান শোনাতে কিংবা প্রচন্ড রোদ্রে মিছিমিছি ঘুড়ে তোকে গল্প শোনাতে; কেউ পারবেনা খুব বেশি রাগ করেও হাসিমুখে আবার তোর সাথে কথা বলতে। কেউ পারবেনা তোকে ভালো জগতে ফিরিয়ে আনতে প্রতিজ্ঞা করতে নিজের জীবনে কে জড়াতে তোর সাথে। আমাদের এ বন্ধুত্বের সম্পর্কে তোর অবদান যে কম তাও কিন্তু নয়।<br />\r\n	<br />\r\n	আমার মনে হয় তোর অবদান আছে অনেক ; পরিবারের সবার চোখ ফাকি দিয়ে রাতদুপুরে কথা বলতে। মা, বাবা&rsquo;র শাসন কে উপেক্ষা করে আমর সাথে সম্পর্ক টিকিয়ে রাখতে; পারিবারিক এ্যালবাম থেকে তুই বেড়িয়ে এসে আমাকে বন্ধু বলে কাছে ডেকেছিস সেইটে কি কম।<br />\r\n	তুই বলতি ওরা ভালো কিছু বলতে জানে; যা হলো একগাদা নীতিবাক্য; যা আজকালকার ছেলেমেয়েরা সহজে মানতে চায়না। ভালো মানুষ হতে গিয়ে তারা নিজেরাই নিজেদের জ্ঞান গুলোকে কাজে লাগাতে চায় ; আর সেটা নাকি আমার কাছে আছে কিন্তু গাধী আমি তো অত বড় বড় কিতাব কখনো পড়িনি যা পড়ে তোকে কিছু জ্ঞান দিতে পারি তবে আমি মানুষকে ভালোবাসার মহামন্ত্র জানি ; জানি কিভাবে দুঃস্বপ্নের হাত থেকেও নিজেকে ফেরানো যায় কিভাবে নিঃশ্বেষ জীবনটাতেও স্বপ্ন দেখানো যায়। আমি ঘৃণা করতে জানিনা।<br />\r\n	<br />\r\n	কি,রে পড়তে পড়তে থমকে গেলি নাকি, ভাবছিস আমি তোকে বেশি বলছি না রে বোকা আমি তোকে খুব কমই বললাম। চিঠি পড়তে গিয়ে মনে হয় একটু ভাবছিস ক্ষণে ক্ষণে তাই না দেখ বেশি ভাবলে চুল ধরে টান দেবো কিন্তু।<br />\r\n	আমি তোর সবচেয়ে অন্যরকম বন্ধু সেটা তুই জানিস, আমার পরিচয় দেবার মতো কিছুই নেই, নেই কোন ব্যকগ্রাউন্ড। তারচেয়ে সত্যি কথা হলো আমি ও রকম হ্যাংলাটে ছেলেদের মতো হতে পারিনি, মেয়েদের দিকে তাকিয়ে থাকতে পারিনা;নিজে যেচে কথা বলা;নারী ভুমিকা বর্জিত জীবনে নারীর প্রতি এখনো লজ্জা কাটিয়ে পারিনি; আর হয়তো কাটবেও না কারণ সে, সুযোগ আর হবেনা।<br />\r\n	তুই আমাকে বলেছিলি, আমি জীবনের প্রতি উদাসীন কেন? আচ্ছা তুই বল এ জীবনে কি পেয়েছি আমি! হাড়ভাঙ্গা খাটুনি খেটে এখনো মায়ের আচঁল তলে পড়ে আছি যে।<br />\r\n	<br />\r\n	আমার স্মৃতিশক্তি খুব প্রখর হলেও মেধা কাটাতে পারিনা আজকাল ধীরে ধীরে আমি মিইয়ে যাচ্ছি,ছোটবেলা থেকে ডাক্তার বা ইঞ্জিনিয়ার হওয়ার স্বাদ আমার কখনো হয়নি। আমাদের বংশে কেউ কোন দিন দু&rsquo;কলম লিখেছে কি,না তার সন্দেহ আছে। প্রিয় কবি জীবনানন্দ; কবি বংশ পরস্পরায়; তার মা ছিলো কুসুম কুমারী দাশ, আর আমার মা; সেটা না হয় আজ নাই বললাম। আমার লেখা যখন যুগান্তরের পতায় প্রকাশ হলো; স্যার আমার হাত ধরে বলেছিলো এ,যে, গোবরে পদ্ম ফুল। আমার সমস্ত সাহিত্যকর্ম মানে কবিতা গল্পের ঝুলিটা যেদিন আগুনে পুড়িয়ে দিয়েছিলাম দীপা বলেছিলো তুমি এমন কেনো!! নিজের সৃষ্টি ধ্বংস করতে কষ্ট হয়না তোমার।<br />\r\n	<br />\r\n	অভি বলল কি সমস্যা তোমার ভাল না লাগে, আমাকে দিয়ে দাও আমি রাখবো , না সেদিন আমি কারো নিষেধ শুনিনি আমি একরোখা হয়ে ধ্বংস করে দিয়েছি সব। সবাই জানে এক কবিতা দু&rsquo;বার লেখা আমার পক্ষ্যে সম্ভব নয়। কারো জন্মদিনে আমি হয়তো দু&rsquo;ছত্র লিখে দিয়েছি, কেউ হয়তো বলেছে ভাই লাইনগুলো আমায় লিখে দাওনা; আমি পারিনা ভেতর থেকে চলে আসে আমার নতুন লাইন পুরোনোটা আর আসেনা।<br />\r\n	আমি সেই ছাত্র; যে স্কুল জীবনে শুধু পাঠ্য বই পড়ে উত্তর লিখতাম; আমি পরীক্ষার আগে কখনো বাংলা বই পড়তাম না আগের মাসে শুধু একটানা পড়ে যাওয়া তারপর পরীক্ষার খাতায় লিখে যাওয়া। স্যার বললেন এ রকম উত্তর তুমি কোথায় পাও আমি বলতাম বিষয় বস্তুর বাইরে তো লিখিনি। তোমার লেখা তো কোন নোটের সাথে মিলেনা; নোট দেখে লিখলে তো মিলবে স্যার। সেই আমি কোথায় হারিয়ে গেছি চুপসে গেছি। এখন মাখা খাটাতে পারিনা !!!!!!!!!!!!<br />\r\n	<br />\r\n	আজকাল দ্বীধাহীন অনেক কিছু করি; কষ্ট হয়না। আমি স্মোকিং করিনা। ছোটবেলায় রেড ওয়াইনের নাম শুনে বলেছিলাম ওটা ছাড়া ড্রিংকস্ নেবনা। আমাদের এ বাজারে ভালো কিছু না পাওয়া গেলেও রাম,ভদকা,সোডা,হুাইস্কি মেলে খুব সহজেই; যে ছেলেটির ছোটবেলায় চামচের অভাবে ভাঙ্গা ঝিনুকে দুধ খেয়ে অভ্যস্থ তার মুখে এমন কথা মানায় বল? কথায় আছেনা গরিবের টাট্টুঘোড়া দৌড়াবার শখ !! আচ্ছা তুই কখনো টাট্টুঘোড়া দেখেছিস ! মনে হয় না। আমিও দেখেনি।<br />\r\n	<br />\r\n	আজ কি আর সেই যুগ আছে বাবা,মা যার কাছে বিয়ে দেবে তাকেই বিয়ে করবে মেয়ে; সব মেয়েই চায় তার লাইফ পার্টনার হবে বুদ্ধিমান,স্মার্ট,হ্যান্ডসাম সর্বোপরি মানান সই। আমি কিন্তু তার ধারে কাছেও নেই সুন্দর্যতা বলতে কিছু আছে কি আমার মাঝে? নেই মিলি আমাকে দেখে হাসতে হাসতে অস্থির, অভি বলে আমি নাকি কালো সুন্দর,তাপ্পু কিংবা সুমন তো বলে এ জীবনে তোর গলায় মালা কে দেবে? আমি কি বলবো বল?<br />\r\n	তবে আমি যেটা পারি তর্ক করতে অঝতাই তর্ক একসময় চার দেয়ালে বন্দি থেকে কথা বলতাম কম কিন্তু এখন চারপাশে খোলা আকাশ পেয়ে আমি কথার ফুলঝুরি ফুটিয়েছি। আর তুই হলি সেই ব্যাক্তি যাকে আমি কথার খোচায় তিনশত পয়ষট্টি দিনে কমপক্ষ্যে ছয়শবার রাগিয়েছি!!! কি রে, সত্যি বলছি না তুই তো বলিস কাছে ফেলে খামচি দিয়ে আমার মাংস খুলে নিবি আর ডাইনোসরটা (রাত্রী) বলে ঘুসি মেরে নাক ফাটিয়ে দেবে আমার। আচ্ছা তোরা কি কখনো রেসলিং প্র্যাকটিস করতি নাকি।<br />\r\n	মমি থাম একটু পড়ে পর!<br />\r\n	<br />\r\n	চিঠি যখন লিখছি মোবাইলে তোর রিং বাজছে; আমি জানি রিসিভ করলেই তোর ঝাড়ি: কেননা কাল রাতে তোকে বলেছিলাম সুখে থাক তোর কাছের মানুষদের নিয়ে; একথা শুনলে তোর রাগ উঠে যায়!! তারপর আমি ফোন রাখার পর দেখি আর টাকা নেই যে কল করবো।<br />\r\n	তোর একটা বিষয়ের সাথে আমার মিল আছে দারুন ভাবে সেটা হলো ফেবারিট সিনেমা টাইটানিক আর সেলন ডয়েলের গাওয়া সেই বিখ্যাত গান<br />\r\n	every night in my dreams i see you i fill you......................................<br />\r\n	সত্যি ভালো লাগে গানটা আমি শুনি তন্ময় হয়ে</p>\r\n<p>\r\n	পরিশেষে,<br />\r\n	সবাই জানে তুই আমার সবচেয়ে ভালো বন্ধু আমার সবকিছু তোর জানা। এ কথাটা আমিও জানি; কিন্তু বন্ধু ; তুই কি এটা জানিস; তোদের জীবন নদীর বালুকা বেলায় আমি ছোট্ট নুড়ি পাথর মাত্র। যে পাথরটি জ্বলজ্বল করছে জ্বলছে সারক্ষণ;কিন্তু যদি কোন পথিকের পায়ের স্পর্শে আবার ডুবে যায় তো খুজে পাওয়া মুস্কিল হবে।<br />\r\n	আমি অনেক বড় চিঠি লিখে ফেলেছি রে জানি পড়তে পড়তে তোর হাত ব্যাথা করছে আর মনে মনে গালি দিচ্ছিস শালা<br />\r\n	চিঠি না লিখে পুরো গল্প লিখে পাঠিয়েছি। খেয়েদেয়ে আর কাজ পায়নি । আসলেই রে আমি তো একটা হাদারাম; খেয়েদেয়ে কোন কাজ নেই।<br />\r\n	<br />\r\n	জানিনা এ চিঠির কোন উত্তর পাবো কিনা। তবে চাইবো তোর ছোট একটা উত্তর বাকিটা তোর ইচ্ছা। তুই কি, রে হাই তুললি কেন ? ঘুম পাচ্ছে? এই রে সেরেছে; এই চিঠি পড়েই তোর ঘুম পাচ্ছে; সে,কি রে! যখন লাভ লেটার পড়তি ঘুম পেতোনা!! শালা ! ন্যাকামো হচ্ছে প্রেম পিরিতির আদিখ্যেতা দেখলে সবার গা জ্বলে আর আমার জ্বলে হাত; নিশপিশ করে চটাং করে চড় দিতে ইচ্ছে করে প্রেম নামক গাব গাছের বিচি নিয়ে এতো ফালাফালি কেন।<br />\r\n	লেখা শেষ করে ফেললাম.......................এই দাড়া আবার তোর ফোন (তুই আমাকে শালা বললি কেন !! আমি তোর কোন জনমের শালা বলতো; মেরে তোর বিষদাত ভেঙ্গে দেবো।)<br />\r\n	<br />\r\n	আজ এখানেই রাখছি; নিজের প্রতি খেয়াল তুই কোনকালেই নিসনা; সেটা আমি জানি তবু বলছি একটু খেয়াল নিলেই তুই আমি ভালো থাকতে পারবো ; এ চিঠি আমি রাতে লিখিনি একলাইনও; সবটা দিনে লিখেছি; আর বিশ সেকেন্ড পড়েই আমি চিঠি লেখা বন্দ করবো তার আগে একটা কথা বাধা যতই আসুক থাকবো বন্ধুত্বের আঙ্গিনায় দু&rsquo;জনা।<br />\r\n	চিঠির শেষে ইতি লেখাটা পুরোনো ষ্টাইল তাই সেটা আর লিখলাম না;বেশি রাত জাগবি না; ঠিক মতো খাবি ঠান্ডা লাগাবি না। আর উপদেশ দিলাম না; ভালো থাকিস তোর ভালো থাকার কামনায়<br />\r\n	&nbsp;</p>\r\n<p>\r\n	<strong>তোর বন্ধু এই আমি।</strong><br />\r\n	<br />\r\n	&nbsp;</p>\r\n', '114', 0, 0, 1, 0, '0000-00-00 00:00:00', 80, '', '2012-09-20 02:39:58', '0000-00-00 00:00:00', 27, 0, '2012-09-23 20:12:35', 1),
  (115, 17, 'blog', 0, 'publish', 0, '', 'ডিজিটাল বাংলাদেশ ২০১২', '<p>\r\n	<img alt="" src="http://media.somewhereinblog.net/images/engineer_kabir_1295205725_1-168612_178019965554741_100000402161872_448499_3060226_n.jpg" style="width: 720px; height: 540px; " />বাংলাদেশের পাওয়ার সংকট আর বেশি দিন স্হায়ী হবেনা তা ছবিগুলো দেখলেই বুঝা যায় । হবিগন্জ জেলার বানিয়াচং হয়ে আজমেরী গন্জ যাওয়ার পথে আপনার চোখে পড়বে এই এনার্জী সেভিং ফেরি । গত ১০-২০ বছর এই অত্যাধুনিক সার্ভিসটি কার্যকর আছে শুধুমাত্র ব্রিজটি সম্পন্ন না হওয়ার কারণে । আশাকরি বিষয়টির উপরে কতৃপক্ষের নজড় দেয়া উচিৎ ।</p>\r\n', '115', 0, 0, 0, 1, '0000-00-00 00:00:00', 132, '', '2012-09-20 09:02:09', '2012-09-23 00:18:57', 19, 19, '2012-09-23 20:16:16', 1);
