  (137, 0, 'episode', 0, 'publish', 9, 'episode three', '', '<p>\r\n	test for episode three. this is line one.</p>\r\n<p>\r\n	this is line two.</p>\r\n<p>\r\n	this is line three.</p>\r\n<p>\r\n	this is line four.</p>\r\n<p>\r\n	this is line five.</p>\r\n', '', 0, 0, 0, 0, '0000-00-00 00:00:00', 0, '', '2012-10-03 12:00:05', '0000-00-00 00:00:00', 35, 1, '0000-00-00 00:00:00', 0),
  (138, 13, 'blog', 0, 'publish', 0, '', 'অফিস, বাসাবাড়ি ও শিল্প কারখানাতে সৌর বিদ্যুৎ স্থাপন নিয়ে বিদ্যুৎ সরবরাহকারী বিভিন্ন প্রতিষ্ঠানের কর্মকর্তা/ কর্মচারীদের রমরমা বাণিজ্য।', '<p>\r\n	সরকারী বিধি অনুযায়ী প্রতিটি অফিস, বাসাবাড়ি ও শিল্প কারখানাতে নতুন বিদ্যুৎ কানেকশন পেতে হলে পূর্বে নির্ধারিত হারে সৌর শক্তি স্থাপন করতে হয়। বিদ্যুৎ অফিসে আবেদন করার পরে বিদ্যুৎ বিতরণ বিভাগ গ্রাহককে চিঠি দিয়ে জানিয়ে দেয় গ্রাহকের কত ওয়াটের সৌর বিদ্যুৎ স্থাপন করতে হবে। গ্রাহক সেই অনুযায়ী সৌর বিদ্যুৎ স্থাপন করে বিদ্যুৎ বিতরণ বিভাগকে অবহিত করলে বিদ্যুৎ বিতরণ বিভাগ তা যাচাই পূর্বক বিদ্যুতের কানেকশন দিয়ে দেয়।&nbsp;<br />\r\n	<br />\r\n	উপরে উল্লেখিত পদ্ধতিটি সঠিক ও নিয়মানুযায়ী। কিন্তু বাস্তবে বেশ অমিল দেখা যাচ্ছে। ধরা যাক কোন নতুন বিল্ডিং এ ২০০০ ওয়াট এর সৌরবিদ্যুৎ স্থাপন করার কথা বলা হয়েছে, এখন বিদ্যুৎ সরবরাহকারী প্রতিষ্ঠানের কিছু কর্মকর্তা ও কর্মচারী গ্রাহককে লোভনীয় অফার দিয়ে বলে কেন এত টাকা খরচ করবেন। আমাদের লোকজন ই তো ইন্সপেকশন করবে। আপনি কম ওয়াটের সৌরবিদ্যুৎ লাগান। বাকিটা আমরা ম্যানেজ করবো।<br />\r\n	<br />\r\n	এভাবেই বিভিন্ন আইনি ফাক ফোকর বের করে রমরমা বাণিজ্য শুরু করে দিয়েছেন বিদ্যুৎ সরবরাহকারী বিভিন্ন প্রতিষ্ঠানের কর্মকর্তা/ কর্মচারীরা। কেউ আবার নামে বেনামে সৌর বিদ্যুৎ/ সাব-স্টেশন এর কোম্পানি খুলে বসেছেন।<img alt="" src="http://www.facebook.com/photo.php?fbid=256938637742596&amp;set=a.246933825409744.36167.232660596837067&amp;type=1&amp;theater" /></p>\r\n', '138', 0, 0, 0, 0, '0000-00-00 00:00:00', 26, 'Array', '2012-10-03 14:26:13', '0000-00-00 00:00:00', 19, 0, '2012-10-03 18:22:39', 1);
INSERT INTO `blogs` (`blog_id`, `blog_category_id`, `post_type`, `status_id`, `status`, `episode_id`, `episode_number`, `title`, `description`, `permalink`, `is_published`, `is_post`, `is_selected`, `sticky_on_home_page`, `publish_date`, `viewed`, `featured_image`, `create_date`, `update_date`, `create_by`, `update_by`, `last_modaretion_date`, `last_modarate_by`) VALUES
  (139, 9, 'blog', 0, 'publish', 0, '', 'নদীর নাম পায়রা', '<p>\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">পায়রা তোমার কাঁদা পানি</span><br />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">আমার কাছে মুক্তা-মণি</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">তোমার জলের ছলাৎ ধ্বনি</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">চোখ মুদিলে আজ ও শুনি ।</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">তোমার দেওয়া পলি দিয়ে</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">সোনার ফসল ফলে</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">কত মানুষ জীবন বাঁচায়</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">ভেসে তোমার জলে ।</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">কভু মিঠা কভু লোনা</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">রূপ যে তোমার কত?</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">তোমার বুবক প্রাণ দিয়েছে</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">মানুস শত-শত ।</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">কারো মুখে ভাত তুলে দাও</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">কাউকে আবার মারো</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">আপন করে পর করে দাও</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">কেমনে তুমি পারো ?</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">তবুও তোমায় ভালোবাসি</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">আমার সুখে-দুঃখে</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">কারন তোমার প্রবাহ ধারা</span><br style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; " />\r\n	<span style="color: rgb(85, 85, 68); font-family: tahoma, ''Trebuchet MS'', lucida, helvetica, sans-serif; font-size: 13px; line-height: 18px; ">জন্ম ভুমির বুকে ।</span></p>\r\n', '139', 0, 0, 0, 0, '0000-00-00 00:00:00', 10, '', '2012-10-03 14:51:57', '0000-00-00 00:00:00', 19, 0, '2012-10-04 10:10:17', 1),
  (140, 9, 'blog', 0, 'publish', 0, '', 'তাই হয়তো না পাওয়ার যন্ত্রনায় বাঁচি', '<p>\r\n	তাই হয়তো না পাওয়ার যন্ত্রনায় বাঁচি<br />\r\n	<br />\r\n	উচাটন মনটা আমার, এদিক ওদিক চায়, আকুল করে জোয়ার ভাটা<br />\r\n	বিরহী বর্ষা কেঁদে কেঁদে যায় ফিরে যায় ।<br />\r\n	শরত মেঘে তোমার ছায়া, শঙ্খচিলের গায়! বিচ্ছিন্ন বাতাস ডাক দিয়ে কয়<br />\r\n	কোথায় সুজন প্রিয়া? পরেছ বেলা সন্ধ্যা মায়া; খুঁজব কোথায় আর,<br />\r\n	সময় যায় বয়ে হাওয়ার বেগের মত ভালবাসবার !সূবর্ণ রেখা যাচ্ছে এঁকে বেঁকে ।<br />\r\n	কোথায় সেই সূবর্ণ দুরন্তপনা চাওয়া? যতই আসুক ঝড় ঝঞ্ঝা, মহা প্রলয় বাঁধা<br />\r\n	রক্ত ঝরিয়ে জয় করতে মনের নাই মানা! কোথায় সুজন প্রিয়া ?<br />\r\n	<br />\r\n	কোথায় স্বপ্ন ! কোথায় বাতাস? আঁধার জাগে রাত্রির পাহারায়,<br />\r\n	শুনশান জোনাক আলো ফোটে ।<br />\r\n	কোথায় আমি ! কোথায় ভাবনা আমার? বিদিশা বিহ্বলতায় আমি প্রকৃতির,<br />\r\n	অসিম শূন্যতা ছুঁয়ে থাকি ।<br />\r\n	কোথায় স্বপ্ন! কোথায় বাতাস? কোথায় আমি! কোথায় ভাবনা আমার?<br />\r\n	শঙ্খচিল উড়ে ঐ নীলাকাশ ছুঁয়ে, আমার মন তার সাথে সাথে;<br />\r\n	শূন্যতার জানলায় দেয় উঁকি প্রতিক্ষণে বিরামহীন শ্রমে ভাবনা খুঁজে ফিরে ।<br />\r\n	<br />\r\n	কি সে খুঁজে? কিসের জন্য তার এত তন্দ্রা ভ্রম?<br />\r\n	বুঝি শুধু একটু! কি জানি পাই আমি তার কাছে;<br />\r\n	তাই হয়তো না পাওয়ার যন্ত্রনায় বাঁচি ।</p>\r\n', '140', 0, 0, 0, 0, '0000-00-00 00:00:00', 37, '', '2012-10-06 17:01:15', '0000-00-00 00:00:00', 23, 0, '2012-10-06 21:15:13', 1);
