INSERT INTO `notices` (`notice_id`, `title`, `description`, `create_date`, `create_by`, `update_date`, `update_by`, `is_valid`, `make_active`, `status`) VALUES
  (1, 'নক্ষত্রব্লগ এর development version ১.৭ প্রকাশিত হয়েছে। নক্ষত্র -এ ব্লগিং করুন।', '<p>\r\n	নক্ষত্র ব্লগ বেটা ১.৭ প্রকাশিত হয়েছে। ব্লগের কারিগরি উন্নয়ন চলছে।&nbsp; ব্লগারদের নতুন ধারার ব্লগিং এর অভিজ্ঞতা দিতে নক্ষত্রব্লগ কাজ করে যাচ্ছে। এখন আপনি আপনার প্রোফাইলকে আপনার নিজের মত করে সাজাতে পারবেন। ব্লগিং এ&nbsp; আনা হচ্ছে আধুনিকতা। মনে রাখবেন মুল ধারার ব্লগে পরিনত হওয়ার জন্য প্রয়োজনীয় সবই নক্ষত্রব্লগ করবে। আমাদের সাথেই থাকুন। লিখুন মতামত দিন। দেশের উন্নয়নে আপনার মুল্যবান ভুমিকা তুলে ধরুন।</p>\r\n', '2012-09-16 18:25:05', 1, '2012-09-27 21:38:56', 1, 1, 1, 0);
